public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("SimpleCat.stl");
	FuncXYZ f = (x, y, z) => sdf.From(x,y,z) - 0.06d;

  	FuncXYZ p1 = (x, y, z) => x - (-0.20);
  	FuncXYZ p2 = (x, y, z) => x - (-0.05);  	
  	FuncXYZ p3 = (x, y, z) => x - (+0.08);
  	FuncXYZ p4 = (x, y, z) => x - (+0.22);
  
  	var b1 = Levels1(f, new FuncXYZ[]{ p1 }, 2, 0.03d, 0.03d, complement: false, fill : false);
  	var b2 = Levels1(f, new FuncXYZ[]{ p1.Not(), p2 }, 2, 0.03d, 0.03d, complement: true, fill : false);
  	var b3 = Levels1(f, new FuncXYZ[]{ p2.Not(), p3 }, 2, 0.03d, 0.03d, complement: false, fill : false);
  	var b4 = Levels1(f, new FuncXYZ[]{ p3.Not(), p4 }, 2, 0.03d, 0.03d, complement: true, fill : false);
  	var b5 = Levels1(f, new FuncXYZ[]{ p4.Not() }, 2, 0.03d, 0.03d, complement: false, fill : false);
  
  
  	/*b1.Transform(delegate(ref double x, ref double y, ref double z)
	{
		x += 0.3;      
    });
  
  	b3.Transform(delegate(ref double x, ref double y, ref double z)
	{
		x -= 0.3;      
    });

  	b4.Transform(delegate(ref double x, ref double y, ref double z)
	{
		x -= 0.6;      
    });

  	b5.Transform(delegate(ref double x, ref double y, ref double z)
	{
		x -= 0.9;      
    });*/
  
  
    var res = new IneqMesh
    {
        X0 = -1.2,
        Y0 = -1.2,
        Z0 = -1.2,
        X1 = 1.5,
        Y1 = 1.2,
        Z1 = 1.1,
        D = 0.1,
      	//MaxCheckCount = 5,
      	//CheckDivisionCount = 2,      
        Boxed = false,
        IneqTree = b4 & !(# y - 0.0)
          & !Cylinder(0, 0.2,-1,  0, 0.2, -0.5, 0.15)
          /*(IneqTree)sdf.From &*/   //b1 | b2 | b3 | b4 | b5
	};
	
  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      
        for (int i = 0; i < 2; i++)
        {
      		res.RefineTetrahedralMesh(res.IneqTree, 2, 0.03, false, 0);
      		res.JiggleBackgroundMash(3);
        }      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);      	
    };
  
  	/*res.PrepareBackgroundMesh = () => 
    {
      	res.FindCrossEdges(0,2);
    };*/  
  
	return res;  
}
