public IneqMesh GetIneqMesh()
{
   	var sdf = new SignedDistance("earth_1_6_10_7.stl");
	var f = ((double x, double y,double z) => 
             {
				var r = sqrt(x*x + y*y +z*z);
               	double rr = 0.99;
               
             	return r - rr + 3*(sdf.From(x, y, z) - r + rr) ;
             }
            );
  	var b = new IneqTree(f) ;
  
    var res = new IneqMesh
    {
        X0 = -1.1,
        Y0 = -1.1,
        Z0 = -1.1,
        X1 = 1.1,
        Y1 = 1.1,
        Z1 = 1.1,
        D = 0.1d,
        Boxed = true,
        IneqTree =
          b //| (# z+0.9)
	};
  
  	//res.ProjectToSurfaceSpec[f] = sdf.Project;
  	res.PrepareBackgroundMeshBeforeApriory = () => res.RefineTetrahedralMesh(0, 20, 0.05);
  
  	return res;
}
