SignedDistance sdf = new SignedDistance("Henne.stl");
SignedDistance sdf1 = new SignedDistance("Hahn.stl");

private IneqTree Muse(double t, double a)
{
	FuncXYZ m =  (x,y,z) => t * sdf.From(x, z, -y) + (1-t) * sdf1.From(x, z, -y);
  
	var b = new IneqTree(m); 
  
	b.Transform(delegate(ref double x, ref double y, ref double z) 
	{
      
      	double x1 = x * cos(a) - z * sin(a);
        double z1 = x * sin(a) + z * cos(a);
        x = x1; z = z1;

		z += 0.7d;      

      	x *= 2d; y *= 2d; z *= 2d;  
    });
  
	return b;  
}

public IneqMesh GetIneqMesh()
{
	int p = 6;
  	IneqTree[] m = new IneqTree[p];
  	IneqTree mm = new IneqTree();
  
  	for(int i=0; i < p; i++)  
    {
		m[i] = Muse((double)i/(p-1), (double)i * 2 * PI / p);
      	mm = mm | m[i];
    }
  
    var res = new IneqMesh
    {
        X0 = -1.2,
        Y0 = -1.2,
        Z0 = -1.2,
        X1 = 1.2,
        Y1 = 1.2,
        Z1 = 1.2,
        D = 0.1,
        Boxed = true,
        IneqTree =
          (/*m[0] | m[1] | m[2] | m[3] | m[4] | m[5]*/ mm | Cylinder(0,0.5,0,0, 0.4,0, 1.17, 0.1))
	};


  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      
		for (int j = 0; j < p; j++)
        {
          for (int i = 0; i < 2; i++)
          {
              res.RefineTetrahedralMesh(j, 2, 0.05, false, 0);
              res.JiggleBackgroundMash(3);
          }
        }
      
      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);      	
    };
  
  	/*res.PrepareBackgroundMesh = () => 
    {
      	res.FindCrossEdges(0,2);
    };*/  
    
	return res;    
}
