public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("horse_baseless.stl");
	FuncXYZ f = (x, y, z) => sdf.From(x,y,z) - 0.02d;
  	FuncXYZ p1 = (x, y, z) => x - (-0.08);
  	FuncXYZ p2 = (x, y, z) => x - (+0.08);
  
  	var b = Levels1(f, new FuncXYZ[]{ p1.Not(), p2 }, 2, 0.01d, 0.01d, complement: false, fill : false);
  
    var res = new IneqMesh
    {
        X0 = -1.2,
        Y0 = -1.2,
        Z0 = -1.2,
        X1 = 1.1,
        Y1 = 1.2,
        Z1 = 1.1,
        D = 0.1,
      	//MaxCheckCount = 5,
      	//CheckDivisionCount = 2,      
        Boxed = false,
        IneqTree = (IneqTree)sdf.From//b
	};
	
  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      
        for (int i = 0; i < 2; i++)
        {
      		res.RefineTetrahedralMesh(res.IneqTree, 2, 0.03, false, 0);
      		res.JiggleBackgroundMash(3);
        }      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);      	
    };
  
  	/*res.PrepareBackgroundMesh = () => 
    {
      	res.FindCrossEdges(0,2);
    };*/  
  
	return res;  
}
