public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("Low_Poly_Dog_Sitting.stl");
	var b = (# sdf.From(x, y, z)-0.01) ;
  
    var res = new IneqMesh
    {
        X0 = -1.2,
        Y0 = -1.2,
        Z0 = -1.2,
        X1 = 1.2,
        Y1 = 1.2,
        Z1 = 1.2,
        D = 0.1d,
        Boxed = false,
        IneqTree =
          b //| (# z+0.9)
	};
  
  	//res.ProjectToSurfaceSpec[f] = sdf.Project;
  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
        for (int i = 0; i < 4; i++)
        {
      		res.RefineTetrahedralMesh(0, 2, 0.03, false, 0.1);
      		res.JiggleBackgroundMash(3);
        }      	
    };
  
  	res.PrepareBackgroundMesh = () => 
    {
      	res.FindCrossEdges(0,2);
    };  
    
  	return res;
}
