public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("v2.stl");
	var f = ((double x, double y,double z) => sdf.From(x, y, z) /*- 0.01*/);
  	var b = new IneqTree(f) ;  
  
    var res = new IneqMesh
    {
        X0 = -1,
        Y0 = -1,
        Z0 = -1,
        X1 = 1,
        Y1 = 1,
        Z1 = 1,
        D = 0.1,
        Boxed = false,
        IneqTree =
          b //| Ball(0,-0.5,-0.3,0.5)
	};
  
  	//res.ProjectToSurfaceSpec[f] = sdf.Project;

  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
		/*res.RefineTetrahedralMeshNearPointRedGreen(new Point[]
		{ 
          p1,
		}, 0.3d, 2);*/
      
        for (int i = 0; i < 3; i++)
        {
      		res.RefineTetrahedralMesh(b, 2, 0.03, false, 0);
      		res.JiggleBackgroundMash(3);
        }
      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);
      	
    };
  
  	/*res.PrepareBackgroundMesh = () => 
    {
      	res.FindCrossEdges(0,2);
      	//res.FindCrossEdges(0,6);
    };*/  
  	return res;
  
}
