public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("Muse.stl");
	var f = sdf.From;
  	var b = new IneqTree(f) ;
  
    var res = new IneqMesh
    {
        X0 = -1.2,
        Y0 = -1.2,
        Z0 = -1.2,
        X1 = 1.2,
        Y1 = 1.2,
        Z1 = 1.2,
        D = 0.04,
        Boxed = false,
        IneqTree =
          (b /*| Ball(0,1.1,0, 0.5,8)*/) //& (# y -1)
          // Torus(0,0.7,0, 0.3, 0.4,  0,1,0) 
	};

  	res.ProjectToSurfaceSpec[f] = sdf.Project;	
  	res.PrepareBackgroundMeshBeforeApriory = () => res.RefineTetrahedralMesh(0, 15, 0.08);
  
	return res;    
}
