SignedDistance sdf = new SignedDistance("pear.stl");

private IneqTree Pear(bool top)
{
 	var b = (# sdf.From(x, y, z));
	var nb = (# sdf.From(x, y, z) + (top ? 0.09 : 0.05));
  
  	var res = b & !nb;
  
  	if(top)
    {
      	res = res & !(# z - sqrt(x*x + y*y) + 0.25);
        res.Transform(delegate(ref double x, ref double y, ref double z)
		{
        	z -= 0.3;      
       	});      
    }
  	else
  	{
      	res = res  & (# z - sqrt(x*x + y*y) + 0.25);
    }

  	return res;
  
}


public IneqMesh GetIneqMesh()
{
      
    var res = new IneqMesh
    {
        X0 = -1,
        Y0 = -1,
        Z0 = -1.2,
        X1 = 1,
        Y1 = 1,
        Z1 = 1.5,
        D = 0.1d,
      	//MaxCheckCount = 5,
        Boxed = true,
        IneqTree = Pear(true) | Pear(false)
	};
  
  	res.PrepareBackgroundMeshBeforeApriory = () => res.RefineTetrahedralMesh(res.IneqTree, 5, 0.1);
  
  	return res;
}
