public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("Shrek Head.stl");
	var f = sdf.From;
  	FuncXYZ p = (x, y, z) => y;
  
  	var b = Levels(f, p, 6, 0.05d, 0.05d, true);
  
    var res = new IneqMesh
    {
        X0 = -1,
        Y0 = -1,
        Z0 = -1,
        X1 = 1,
        Y1 = 1,
        Z1 = 1,
        D = 0.09,
      	MaxCheckCount = 5,
      	CheckDivisionCount = 2,      
        Boxed = false,
        IneqTree = b
	};
	
  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      
        for (int i = 0; i < 3; i++)
        {
      		res.RefineTetrahedralMesh(res.IneqTree, 2, 0.03, false, 0);
      		res.JiggleBackgroundMash(3);
        }      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);      	
    };
  
  	res.PrepareBackgroundMesh = () => 
    {
      	res.FindCrossEdges(0,2);
    };  
  
	return res;  
}
