public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("SimpleCat.stl");
	var b = (# sdf.From(x, y, z)) ;
  	var sdf1 = new SignedDistance("teddybear3dizzy.stl");
	var b1 = (# sdf1.From(x, y, z)) ;
  
  
    var res = new IneqMesh
    {
        X0 = -1,
        Y0 = -1,
        Z0 = -1,
        X1 = 1,
        Y1 = 1,
        Z1 = 1,
        D = 0.05,
        Boxed = false,
        IneqTree =
          b | b1 //| Ball(0,-0.5,-0.3,0.5)
	};
  
  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      
        for (int i = 0; i < 3; i++)
        {
      		res.RefineTetrahedralMesh(1, 2, 0.03, false, false);
      		res.JiggleBackgroundMash(3);
        }      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);      	
    };
  
	return res;  
}
