private static SignedDistance sdf = new SignedDistance("Trump.stl");
private static SignedDistance sdf1 = new SignedDistance("Kung_Fu_Panda_po.stl");  

public IneqMesh GetIneqMesh()
{
	var f = ((double x, double y,double z) => sdf.From(x, y, z));  
  	var b = new IneqTree(f) ;
  
	var f1 = ((double x, double y,double z) => sdf1.From(x, y, z));  
  	var b1 = new IneqTree(f1) ;
  
	b1.Transform(delegate(ref double x, ref double y, ref double z) 
    {
      	z -= 1.6;
      	x -= 0.5;
      
        double a = -PI/2;	
        double y1 = y * cos(a) - z * sin(a);
        double z1 = y * sin(a) + z * cos(a);
        y = y1; z = z1;
      
		x *= 1.25;
      	y *= 1.25;
		z *= 1.25;      
		      
    });
  
  
    var res = new IneqMesh
    {
        X0 = -1,
        Y0 = -1,
        Z0 = -0.8,
        X1 = 1.3,
        Y1 = 1,
        Z1 = 3,
        D = 0.1d,
        Boxed = true,
        IneqTree =
          b | b1 | Ball(0,0,-0.85,0.95)
	};
  
  	//res.ProjectToSurfaceSpec[f] = sdf.Project;
	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
        for (int i = 0; i < 3; i++)
        {
      		res.RefineTetrahedralMesh(0, 2, 0.05, false);
          	res.RefineTetrahedralMesh(1, 2, 0.03, false);
      		res.JiggleBackgroundMash(3);
        }
      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);
      	
    };
  
  	/*res.PrepareBackgroundMesh = () => 
    {
      	res.FindCrossEdges(0,2);
      	//res.FindCrossEdges(0,6);
    };*/  
  
  	return res;
}
