public IneqMesh GetIneqMesh()
{
   	var sdf = new SignedDistance("lego_earth.STL");
	var f = ((double x, double y,double z) => sdf.From(x, y, z));
  	var b = new IneqTree(f) ;
  
    var res = new IneqMesh
    {
        X0 = -1,
        Y0 = -1,
        Z0 = -1,
        X1 = 1,
        Y1 = 1,
        Z1 = 1,
        D = 0.1d,
        Boxed = false,
        IneqTree =
          b //| (# z+0.9)
	};
  
  	res.ProjectToSurfaceSpec[f] = sdf.Project;
  	res.PrepareBackgroundMeshBeforeApriory = () => res.RefineTetrahedralMesh(0, 10, 0.1);
  
  	return res;
}
