[advanced]
public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.2,
        Z0 = -1.3,
        X1 = 1.0,
        Y1 = 1.2,
        Z1 = 1.3,
        D = 0.1d,
        Boxed = false,
        IneqTree = IneqTreeParser.FromFormula(@"
        (
            (2*x^2+y^2+z^2-1)^3<(0.1*x^2+y^2)*z^3 &&
            x<0.25
        ) ||
        (x^2+y^2+(z-0.2)^2<0.25)
        ")
    };
}
