public IneqMesh GetIneqMesh()
{
  	FuncXYZ f = (x, y, z) => sqrt(x*x + y*y + z*z) -1;
  	FuncXYZ p = (x, y, z) => x;
  
  	var b1 = Levels(f, p, 5, 0.05d, 0.05d, false, 0, false);
  	var b2 = Levels(f, p, 5, 0.05d, 0.05d, true, 0, true);
  
  	b2.Transform(delegate(ref double x, ref double y, ref double z)
	{
		x -= 0.08;      
    });
  
  
    return new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.5,
        Y1 = 0.0,
        Z1 = 1.0,
        D = 0.1d,
        Boxed = true,
        IneqTree = b1 | b2
      };
}
