public IneqMesh GetIneqMesh()
{
  	FuncXYZ f = (x, y, z) => sqrt(x*x + y*y + z*z) -1;

  	FuncXYZ p1 = (x, y, z) => x - (-0.5);
  	FuncXYZ p2 = (x, y, z) => x - 0;
  	FuncXYZ p3 = (x, y, z) => x - 0.5;
  
  	var b1 = Levels1(f, 
                     new FuncXYZ[] {p1}, 
                     3, 0.05d, 0.05d, false, true);
  	var b2 = Levels1(f, 
                     new FuncXYZ[] {p1.Not(), p2}, 
                     3, 0.05d, 0.05d, true, true);
  
  	b2.Transform(delegate(ref double x, ref double y, ref double z)
	{
		x -= 0.3;      
    });
  
  
    return new IneqMesh
    {
        X0 = -1.5,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.5,
        Y1 = 1.0,
        Z1 = 1.0,
        D = 0.1d,
        Boxed = true,
        IneqTree = b1 | b2
      };
}
