public IneqMesh GetIneqMesh()
{
    var res = new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.0,
        Y1 = 1.0,
        Z1 = 1.0,
        D = 0.3d,
        Boxed = false,
        IneqTree =
          Ball(0, 0, 0, 0.75d) | Ball(0, 0, 0.75d, 0.25d)
      };
  
	res.PrepareBackgroundMesh = () => 
    {
		res.RefineTetrahedralMeshNearPointRedGreen(new Point(0, 0, 0.75d), 0.25);
    };
  
  	return res;    
}
