public IneqMesh GetIneqMesh()
{
	double r = 0.08d;
  	double a = 0.25d;
  
  	var res = new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.0,
        Y1 = 1.0,
        Z1 = 1.0,
        D = 0.2d,
        Boxed = false,
        IneqTree =
          Ball(0, 0, 0, 0.75d) | 
          Ball(0, 0.75d, 0, r, 4) |
          Ball(0, 0.75d * cos(a), 0.75d * sin(a), r)
          
      };
  
	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
		res.RefineTetrahedralMeshNearPointRedGreen(new Point[]
		{ 
          new Point(0, 0.75d, 0),
          new Point(0, 0.75d * cos(a), 0.75d * sin(a))
		}, r, 4);
      
    };
  
  	return res;    
}
