public IneqTree TorusPQ(double x0, double y0, double z0, double r, double R)
{

    return new IneqTree((x, y, z) =>
    {
		double p, q;

      	x = x - x0;
        y = y - y0;
        z = z - z0;      

      	p = z < 0 ? 2 : 6;
      	q = 8;
      
      	double pn = pow(pow(abs(x), p) + pow(abs(z), p), 1.0d/p);
      
      	double sx =  R * x / pn;
      	double sz =  R * z / pn;

		double v = sqrt((x-sx)*(x-sx) + (z-sz)*(z-sz));
      
      	return pow(pow(y, q) + pow(2*v, q), 1.0/q) - r;

    });
}

public static IneqTree Balls(int count, int count1, double R, double r, double p = 2)
{
    IneqTree res = new IneqTree();

	for (int j = 0; j < count1; j++)
    {
      	double z0 = j * 0.27d;
        for (int i = 0; i < count; i++)
        {
			double a = ((double)i + ((double)(j % 2))/2.0d) * 2 * PI / count;
          	double x0 = R * cos(a);
            double y0 = R * sin(a);

            res = res | ((x, y, z) => pow(Math.Abs(x - x0), p) +
                pow(Math.Abs(y - y0), p) +
                pow(Math.Abs(z - z0), p) -
                pow(r, p));
        }
    }

    return res;
}
