public IneqMesh GetIneqMesh()
{
    double[] X = { 0,     0.5,  1,     1.7,  2 };
    double[] Y = { 0.55,  0.58, 0.55,  0.18, 0.15 };
  
  	var  c1 = Cylinder(-1, 0, 0, 1, 0, 0, rf: Interpolation.CubicSpline(X,Y));
  	var  c2 = Cylinder(-0.95, 0, 0, 1.05, 0, 0, rf: Interpolation.CubicSpline(X,Y.Select(x => x-0.05).ToArray()));  
  
    return new IneqMesh
    {
        X0 = -1.1,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.0,
        Y1 = 1.2,
        Z1 = 1.0,
        D = 0.08d,
        Boxed = false,
        IneqTree = (c1.ToFunc(0.03) & !c2)//.ToFunc(0.01) //& Ball(0,0,0,0.5)
      };
}
