static double[] X = { 0,     0.5,  1,    1.35, 1.7,  1.85, 2 };
static double[] Y = { 0.55,  0.58, 0.55, 0.36, 0.17, 0.15, 0.15 };

private Func<double,double> outer = Interpolation.CubicSpline(X,Y);
private Func<double,double> inner = Interpolation.CubicSpline(X.Select(x => x-0.05).ToArray(), 
															  Y.Select(y => y-0.05).ToArray());

private IneqTree Lid()
{
	var res = new IneqTree();
  
  	res = res | Cylinder(0, 0, 1.1, 0, 0, 1.5, rf: z => 0.08 + 0.04 * (z/0.3));
  
  	var p = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron(0.25).ToIneqTree();  
  
	p.Transform(delegate(ref double x, ref double y, ref double z) 
	{
		z -= 1.6;      
    });  
  
  	res = res | p;
  
	return res;
}


private IneqTree Grape(double z0)
{
	var res = new IneqTree();
  
  	int[] c = {3, 4, 3, 2, 1} ;
  
  	double da = PI / 12;
  	double dz = 0.13;
  	double sa = 0;
	
  	for(int i=0; i < c.Length; i++)
    {
	  double z = z0 - i * dz;			      
      for(int j = 0; j < c[i]; j++)
      {
          double a = sa + j * da;
          double x = 0.95 * outer(z + 1) * cos(a);
          double y = 0.95 * outer(z + 1) * sin(a);
          res = res | Ball(x, y, z, 0.1);
      }
      sa += (i==0 ? -da/2 : da/2);
    }
  
	return res;
}

public IneqMesh GetIneqMesh()
{
  	var  c1 = Cylinder(0, 0, -1   , 0, 0, 1,    rf: outer);
  	var  c2 = Cylinder(0, 0, -0.95, 0, 0, 1.05, rf: inner);  
  
    return new IneqMesh
    {
        X0 = -0.7,
        Y0 = -0.7,
        Z0 = -1.1,
        X1 = 0.7,
        Y1 = 0.7,
        Z1 = 1.9,
        D = 0.05d,
        Boxed = false,
        IneqTree = ((c1.ToFunc(0.03) | Grape(-0.1)) & !c2) | Lid()
      };
}
