private IneqTree Star()
{
    var res = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron(0.4).ToIneqTreeStar(3.1, 0.8); 

    res.Transform(delegate(ref double x, ref double y, ref double z)
	{
      z -= 1.2;
      
      x *= 4.0; y *= 4.0; z *= 4.0;
      
      double a = (PI - acos(-sqrt(5)/5))/2;

      double y1 = y * cos(a) - z * sin(a);
      double z1 =y * sin(a) + z * cos(a);
      y = y1; z = z1;         
      
    });
  
  	return res;
}

public static IneqTree Planes(int count, double r)
{
    IneqTree res = new IneqTree();

    for (int i = 0; i < count; i++)
    {
        double x0 = r * Math.Cos(PI/10 + i * 2 * Math.PI / count);
        double y0 = r * Math.Sin(PI/10 + i * 2 * Math.PI / count);
        double z0 = 0;

        res = res & ((x, y, z) => (x - x0) * x0 + (y - y0) * y0 + (z - z0) * z0);
    }

    return res;
}

private IneqTree Tree()
{
  var res = new IneqTree();
  var b = (IneqLib.Planes1(12) & (# -z - 0.1)) | (Planes(8, 0.15d) & (# z-0.3)) ;

  b.Transform(delegate(ref double x, ref double y, ref double z)
              {
                double a = 6*z;
                double x1 = x * cos(a) - y * sin(a);
                double y1 = x * sin(a) + y * cos(a);
                x = x1; y = y1;

                x *= 2;
                y *= 2;

              });      
  res = res | b;

  return res;

}




public IneqMesh GetIneqMesh()
{ 
    var res = new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -0.6,
        X1 = 1.0,
        Y1 = 1.0,
        Z1 = 1.5,
        D = 0.08d,
        Boxed = true,
        IneqTree = Tree() |
          (Planes(12, 0.35d) & (#z + 0.5))
          
          | Star()
          
          /*.ToFunc(0.02)*/ //| Star()          
      };
  
  res.PrepareBackgroundMeshBeforeApriory = () => 
    {
		res.RefineTetrahedralMeshNearPointRedGreen(new Point[]
		{ 
          new Point(0,0,1.1),
		}, 0.2d, 1);
      
      
    };
  
  	return res;
}
