SignedDistance sdf = new SignedDistance("Dodecahedron.stl", 0);

private IneqTree Balls(double dz, double f, double da = 0)
{
	var res = new IneqTree();	

  	var pol = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron(1);
  
  	foreach (var e in pol.Edges)
    {
		var v1 = pol.Vertices[e.Item1];
      	var v2 = pol.Vertices[e.Item2];
      
        double a = -(PI - acos(-sqrt(5)/5))/2;
        double tz1 = v1.Y * sin(a) + v1.Z * cos(a);
		double tz2 = v2.Y * sin(a) + v2.Z * cos(a);            
      
		if(dz>0 && abs(tz1 - tz2) <0.001 && tz1 < 0)
          continue;
      
      	res = res | Cylinder(v1.X, v1.Y, v1.Z, v2.X, v2.Y, v2.Z, 0.06/*, 0.55*/);
    }
  
  
  	foreach (var face in pol.Faces)
    {
      var vx = face.Sum(i => pol.Vertices[i].X) / face.Length;
      var vy = face.Sum(i => pol.Vertices[i].Y) / face.Length;
      var vz = face.Sum(i => pol.Vertices[i].Z) / face.Length;
      
      double a = -(PI - acos(-sqrt(5)/5))/2;
      double y1 = vy * cos(a) - vz * sin(a);
      double z1 =vy * sin(a) + vz * cos(a);
      

      /*if(abs(vx + y1) < 0.001d && )
        continue;*/
      
      
      if(abs(vx + y1) < 0.001d && z1 + dz<3)
        continue;
      
      if(z1 + dz>3)
      {
        /*for(int k=0; k < face.Length; k++ )
        {
          var i = face[k];
          var ii = face[(k+1) % face.Length];
          
          var sx1 = pol.Vertices[i].X + 0.9 * vx;
          var sy1 = pol.Vertices[i].Y + 0.9 * vy;
          var sz1 = pol.Vertices[i].Z + 0.9 * vz;
          
          var sx2 = pol.Vertices[ii].X + 0.9 * vx;
          var sy2 = pol.Vertices[ii].Y + 0.9 * vy;
          var sz2 = pol.Vertices[ii].Z + 0.9 * vz;
          
          
          res = res | Cylinder(pol.Vertices[i].X, pol.Vertices[i].Y, pol.Vertices[i].Z, sx1, sy1, sz1, 0.06)
            	| Cylinder(sx1, sy1, sz1, sx2, sy2, sz2, 0.06);
        }*/
         res = res | Cylinder(vx, vy, vz, 2 * vx, 2 * vy, 2 * vz, 0.55 );
      }
      else
      {
        if(dz>2 && vx>0 && vy<0)
        {
          //vx *= 1.9d; vy *= 1.9d; vz -= 0.5d;
          res = res | Ball(vx,vy,vz,0.3);
        }
        
        foreach (var i in face)
        {
          res = res | Cylinder(vx, vy, vz, pol.Vertices[i].X, pol.Vertices[i].Y, pol.Vertices[i].Z, 0.06);
        }
      }		        
    }
    	
  
  	res = res.ToFunc(0.03);
  
    /*foreach (var v in vlist)
    {
		res = res | Ball(v.X, v.Y, v.Z, 0.25);
    }*/
  
	res.Transform(delegate(ref double x, ref double y, ref double z)
	{
		double d = sdf.From(x,y,z);
      	double n = sqrt(x * x + y * y + z * z);
      
      	if(n == 0)
			return;          	
      
      	double xp = x - d*x / n;
      	double yp = y - d*y / n;
      	double zp = z - d*z / n;
      
		double dp = sqrt(xp * xp + yp * yp + zp * zp);
      
      	x *= dp; y *= dp; z *= dp; 
      
    });
  
    res.Transform(delegate(ref double x, ref double y, ref double z)
	{

      
      double a = (PI - acos(-sqrt(5)/5))/2;

      double y1 = y * cos(a) - z * sin(a);
      double z1 =y * sin(a) + z * cos(a);
      y = y1; z = z1;         
      
    }); 
  
    res.Transform(delegate(ref double x, ref double y, ref double z)
	{
      double x1 = x * cos(da) - y * sin(da);
      double y1 =x * sin(da) + y * cos(da);
      x = x1; y = y1;         
      
    });   

    res.Transform(delegate(ref double x, ref double y, ref double z)
	{
		z -= dz;
      	x /= f; y /= f; z /= f; 
      
    });  
  
  
  return res;
}

public IneqMesh GetIneqMesh()
{
