        Y0 = -1.1,
        Z0 = -1.2,// + 3,
        X1 = 1.1,
        Y1 = 1.1,
        Z1 = 5.0,
        D = 0.035d,
        Boxed = true,
        IneqTree = Balls(0,1) | Balls(1.47,0.8, PI/5)  | Balls(2.63, 0.6) |
          Torus(0,0,3.78,0.06, 0.2, sqrt(2)/2, sqrt(2)/2, 0)
	};
  
  	//res.RefineTetrahedralMesh(2, 1, 0.03, false, 0);
  
	/*res.PrepareBackgroundMeshBeforeApriory = () => 
    {

      
        for (int i = 0; i < 2; i++)
        {
      		res.RefineTetrahedralMesh(0, 2, 0.03, false, 0);
      		res.JiggleBackgroundMash(3);
        }
      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);
      	
    };*/
  
  	return res;
}
