private IneqTree StarBall()
{
	IneqTree res = /*Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron(1).ToIneqTree()
      & !Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron(0.95).ToIneqTree();*/
      Ball(0,0,0,1) & !Ball(0,0,0,0.95);

  	var pol = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron(1);
  
  	foreach (var face in pol.Faces.Where((item, index) => index != 4)/*.Take(5)*/)
    {

      	var vx = face.Sum(i => pol.Vertices[i].X) / face.Length;
      	var vy = face.Sum(i => pol.Vertices[i].Y) / face.Length;
      	var vz = face.Sum(i => pol.Vertices[i].Z) / face.Length;      	
      
      	var v0x = vx - pol.Vertices[face[0]].X;
      	var v0y = vy - pol.Vertices[face[0]].Y;
      	var v0z = vz - pol.Vertices[face[0]].Z;
      	var nv = sqrt(v0x*v0x +v0y*v0y +v0z*v0z);
      
      	res = res | PrismStar(0.8*vx, 0.8*vy, 0.8*vz, 1.3*vx, 1.3*vy, 1.3*vz, 0.4, 6, 0.35, v: new double[]{v0x/nv, v0y/nv, v0z/nv}/*, sa: PI/5*/);
      
    }    	
  
    res.Transform(delegate(ref double x, ref double y, ref double z)
	{
      double a = (PI - acos(-sqrt(5)/5))/2;

      double y1 = y * cos(a) - z * sin(a);
      double z1 =y * sin(a) + z * cos(a);
      y = y1; z = z1;         
      
    });
  
  return res;
}

public IneqMesh GetIneqMesh()
{
    var res = new IneqMesh
    {
        X0 = -1.1,
        Y0 = -1.1,
        Z0 = -1.1,
        X1 = 1.1,
        Y1 = 1.1,
        Z1 = 1.3,
        D = 0.1d,
        Boxed = true,
        IneqTree = Torus(0,0,1.0, 0.05, 0.15, 0,1,0) | 
          //Cylinder(0,0,0.85 - 0.15,0,0,1.1 - 0.15,0.25) |
         StarBall() 
				
	};
 
  
	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      
		res.RefineTetrahedralMeshNearPointRedGreen(new Point[]
		{ 
          new Point(0,0,0.9),
		}, 0.2d, 2);      
    };
  
  	return res;
}
