SignedDistance sdf = new SignedDistance("Dodecahedron.stl", 0);

private IneqTree StarBall(double r, double r1)
{
	IneqTree res;// = new IneqTree();	
	WireFrameDistance wfd = new WireFrameDistance();
	WireFrameDistance wfd1 = new WireFrameDistance();
  

  	var pol = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron(1);
  
  	foreach (var e in pol.Edges)
    {
		var v1 = pol.Vertices[e.Item1];
      	var v2 = pol.Vertices[e.Item2];
      
      	//res = res | Cylinder(v1.X, v1.Y, v1.Z, v2.X, v2.Y, v2.Z, r);
      	wfd.AddEdge(v1.X, v1.Y, v1.Z, v2.X, v2.Y, v2.Z);
    }
  
  
  	foreach (var face in pol.Faces)
    {
      var vx = face.Sum(i => pol.Vertices[i].X) / face.Length;
      var vy = face.Sum(i => pol.Vertices[i].Y) / face.Length;
      var vz = face.Sum(i => pol.Vertices[i].Z) / face.Length;
      
		for(int k=0; k < face.Length; k++ )
        {
          var i = face[k];
          var ii = face[(k+1) % face.Length];

          var sx1 = pol.Vertices[i].X;
          var sy1 = pol.Vertices[i].Y;
          var sz1 = pol.Vertices[i].Z;

          var sx2 = pol.Vertices[ii].X;
          var sy2 = pol.Vertices[ii].Y;
          var sz2 = pol.Vertices[ii].Z;

          wfd1.AddEdge((sx1+sx2)/2, (sy1+sy2)/2, (sz1+sz2)/2, (sx1+2*vx)/3, (sy1+2*vy)/3, (sz1+2*vz)/3);			
          wfd1.AddEdge((sx1+sx2)/2, (sy1+sy2)/2, (sz1+sz2)/2, (sx2+2*vx)/3, (sy2+2*vy)/3, (sz2+2*vz)/3);			          
        }      
      
      
      /*foreach (var i in face)
      {
        //res = res | Cylinder(vx, vy, vz, pol.Vertices[i].X, pol.Vertices[i].Y, pol.Vertices[i].Z, r);
        wfd.AddEdge(vx, vy, vz, pol.Vertices[i].X, pol.Vertices[i].Y, pol.Vertices[i].Z);
      }*/
    }    	
  
  	res = (# wfd.From(x,y,z) - r) | (# wfd1.From(x,y,z) - r1);
  
	res.Transform(delegate(ref double x, ref double y, ref double z)
	{
		double d = sdf.From(x,y,z);
      	double n = sqrt(x * x + y * y + z * z);
      
      	if(n == 0)
			return;          	
      
      	double xp = x - d*x / n;
      	double yp = y - d*y / n;
      	double zp = z - d*z / n;
      
		double dp = sqrt(xp * xp + yp * yp + zp * zp);
      
      	x *= dp; y *= dp; z *= dp; 
      
    });
  
    res.Transform(delegate(ref double x, ref double y, ref double z)
	{
      double a = (PI - acos(-sqrt(5)/5))/2;

      double y1 = y * cos(a) - z * sin(a);
      double z1 =y * sin(a) + z * cos(a);
      y = y1; z = z1;         
      
    }); 
  
  return res;
}

