        Y0 = -1.1,
        Z0 = -1.1,
        X1 = 1.1,
        Y1 = 1.1,
        Z1 = 1.3,
        D = 0.035d,
        Boxed = true,
        IneqTree = StarBall(0.065, 0.035) | Cylinder(0,0,0.85,0,0,1.1,0.25) | Torus(0,0,1.1, 0.05, 0.15, 0,1,0)
	};
 
  
