private IneqTree Balls()
{
	var res = new IneqTree();	

  	var pol = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateIcosahedron();
  
  	foreach (var e in pol.Edges)
    {
		var v1 = pol.Vertices[e.Item1];
      	var v2 = pol.Vertices[e.Item2];
      	res = res | Cylinder(v1.X, v1.Y, v1.Z, v2.X, v2.Y, v2.Z, 0.08);
    }
  
	double factor=3;
  
	var vlist = new List<Point>();
  
  	foreach (var face in pol.Faces)
    {
      var vx = factor * face.Sum(i => pol.Vertices[i].X) / face.Length;
      var vy = factor * face.Sum(i => pol.Vertices[i].Y) / face.Length;
      var vz = factor * face.Sum(i => pol.Vertices[i].Z) / face.Length;

      vlist.Add(new Point(vx, vy, vz));
      
      //res = res | Ball(vx, vy, vz, 0.05);
      
      foreach (var i in face)
      {
        res = res | Cylinder(vx, vy, vz, pol.Vertices[i].X, pol.Vertices[i].Y, pol.Vertices[i].Z, 0.08);
      }
    }
  
  	/*FuncXYZ f = (x,y,z) =>
    {
      double res = 0;
      foreach(var p in vlist)
      {
        //double d = Math.Sqrt((x-p.X)*(x-p.X) + (y-p.Y)*(y-p.Y) + (z-p.Z)*(z-p.Z));
        double dp = x*p.X + y*p.Y + z*p.Z;
        double ppx = x - dp * p.X;
        double ppy = y - dp * p.Y;
        double ppz = z - dp * p.Z;
        
        double d = sqrt(ppx*ppx + ppy*ppy + ppz*ppz);
        
        res += 0.07d * Math.Exp(-70*d*d);
      }
      return res;
    };
  
  	res.Transform(delegate(ref double x, ref double y, ref double z)
	{
      double e = f(x,y,z);
      
      x /= 1 + e;
      y /= 1 + e;
      z /= 1 + e;
      
    });*/
  
  	res = res.ToFunc(0.01);
  
    foreach (var v in vlist)
    {
		res = res | Ball(v.X, v.Y, v.Z, 0.25);
    }
  
  
   	return res;
}

public IneqMesh GetIneqMesh()
{
    var res = new IneqMesh
    {
        X0 = -2.6,
        Y0 = -2.6,
        Z0 = -2.6,
        X1 = 2.6,
        Y1 = 2.6,
        Z1 = 2.6,
        D = 0.1d,
        Boxed = true,
        IneqTree = Balls()
	};
  
	res.PrepareBackgroundMeshBeforeApriory = () => 
    {

      
        for (int i = 0; i < 2; i++)
        {
      		res.RefineTetrahedralMesh(0, 2, 0.03, false, 0);
      		res.JiggleBackgroundMash(3);
        }
      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);
      	
    };
  
  	return res;
}
