[advanced]
public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = 0,
        X1 = 1.0,
        Y1 = 1.0,
        Z1 = 2.5,
        D = 0.1d,
        Boxed = false,
        IneqTree =
          (
          (IneqTree)
          ((x, y, z) => x * x + y * y - z * z / 4) 
        ) &
          (IneqTree)((x, y, z) => -z + 0.4)
          &
          (IneqTree)((x, y, z) => x * x + y * y + (z * z) / 2 - 2.25 )
      };
}
