public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("Earth.stl");
  
  	FuncXYZ f = (x, y, z) => {
      double s1 = 1.2;
      double s2 = 1.7;
      double s = s1 + (s2-s1)*(z+1)/2;
      x *= s; y *= s;
      return sdf.From(x,y,z);
    };
  
    FuncXYZ fb = (x, y, z) => {
      double s1 = 1.2;
      double s2 = 1.7;
      double s = s1 + (s2-s1)*(z+1)/2;
      x *= s; y *= s;      
      
      return sqrt(x*x + y*y + z*z) - 1.1;
    };
  
  	FuncXYZ p1 = (x, y, z) => x;
  	FuncXYZ p2 = (x, y, z) => y;
  
  	var b = Levels1(fb, new FuncXYZ[] {p2.Not()}, 2, 0.05d, 0.05d, true, true); 
    
    var res = new IneqMesh
    {
        X0 = -1.3,
        Y0 = -1.3,
        Z0 = -1.3,
        X1 = 1.3,
        Y1 = 1.3,
        Z1 = 1.3,
        D = 0.08,
      	//MaxCheckCount = 5,
      	//CheckDivisionCount = 2,      
        Boxed = false,
        IneqTree = b & !(IneqTree)f & !Cylinder(0,0,-1.5, 0,0,-0.5, 0.2) //& !Torus(0, 0, 0, 0.08, 0.84, 0, 0, 1)
	};
	
  
  	/*res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      
        for (int i = 0; i < 2; i++)
        {
      		res.RefineTetrahedralMesh(res.IneqTree, 2, 0.03, false, 0);
      		res.JiggleBackgroundMash(3);
        }      
      	//res.RefineTetrahedralMesh(0, 1, 0.05);      	
    };*/
  
  	/*res.PrepareBackgroundMesh = () => 
    {
      	res.FindCrossEdges(0,2);
    };*/  
  
	return res;  
}
