public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("Earth.stl");
	FuncXYZ f = (x, y, z) => sdf.From(x,y,z);    
  
    FuncXYZ fb = (x, y, z) => sqrt(x*x + y*y + z*z) - 1.15;
  	FuncXYZ p1 = (x, y, z) => x;
  	FuncXYZ p2 = (x, y, z) => y;
  
  	var b = Levels1(fb, new FuncXYZ[] {p1, p2}, 3, 0.05d, 0.05d, true, true); 
    
    var res = new IneqMesh
    {
        X0 = -1.3,
        Y0 = -1.3,
        Z0 = -1.3,
        X1 = 1.3,
        Y1 = 1.3,
        Z1 = 1.3,
        D = 0.08,
      	//MaxCheckCount = 5,
      	//CheckDivisionCount = 2,      
        Boxed = false,
        IneqTree = b & !(IneqTree)f & !Cylinder(0,0,-1.5, 0,0,-0.5, 0.2) & !Torus(0, 0, 0, 0.09, 1.2, 0, 0, 1)
	};
	
  
  	/*res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      
