public IneqMesh GetIneqMesh()
{
   	var sdf = new SignedDistance("earth.stl");
	var f = sdf.From;//((double x, double y,double z) => x*x + y*y + z*z -1 );
  	var b = new IneqTree(f) ;

  	var sdf1 = new SignedDistance("cute-dragon.stl");
	var f1 = ((double x, double y,double z) => sdf1.From(x, y, z));
  	var b1 = new IneqTree(f1) ;  
  
	b.Transform(delegate(ref double x, ref double y, ref double z) 
	{
		double s1 = 1.2;
      	double s2 = 1.7;
      
      	double s = s1 + (s2-s1)*(z+1)/2;
      
      	x *= s; y *= s; //z *= 2.0d;  
    });    
  
	b1.Transform(delegate(ref double x, ref double y, ref double z) 
	{      
        double a = 0.9*PI;
      	double x1 = x * cos(a) - y * sin(a);
        double y1 =x * sin(a) + y * cos(a);
        x = x1; y = y1;

      	double s = 1.08d;      
      	x *= s; y *= s; z *= s;  
    });    
  
  
    var res = new IneqMesh
    {
        X0 = -1.1,
        Y0 = -1.1,
        Z0 = -1.2,
        X1 = 1.1,
        Y1 = 1.1,
        Z1 = 1,
        D = 0.08d,
        Boxed = true,
        IneqTree =
          b | b1| Cylinder(0,0,-0.9,0,0,-1.1,1, 0.1)
	};
  
  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {      
        for (int i = 0; i < 5; i++)
        {
      		res.RefineTetrahedralMesh(0, 2, 0.05, false, 0);
          	res.RefineTetrahedralMesh(1, 2, 0.05, false, 0);
      		res.JiggleBackgroundMash(3);
        } 
    };      
  
  	return res;
}
