[advanced]

private SignedDistance henne = new SignedDistance("Henne.stl");
private SignedDistance hahn = new SignedDistance("Hahn.stl");
private SignedDistance estbunny = new SignedDistance("Estbunny.stl");

public IneqTree Creature(SignedDistance which, double x, double y, double z)
{
    IneqTree res = new IneqTree();
    Point p = new Point(x, y, z);
  
  	double n = sqrt(x*x + y*y +z*z);
  	double nx = x/n;
  	double ny = y/n;
  	double nz = z/n;
  
    p.X += 0.3 * nx;
    p.Y += 0.3 * ny;
    p.Z += 0.3 * nz;

    var m = ComputeBasis(nx, ny, nz, 'z');

    res = res | new IneqTree((x, y, z) =>
    {
        Transform(ref x, ref y, ref z, p, m);
      
      	if(which == hahn)
        {
			double a = PI / 2;	
          	double x1 = x * cos(a) - y * sin(a);
          	double y1 = x * sin(a) + y * cos(a);
          	x = x1; y = y1;
        }

        x = x * 3;
        y = y * 3;
        z = z * 3;

        return which.From(x,y,z);
    });
    return res;
}

public IneqTree Egg(double x, double y, double z, char axis)
{
    IneqTree res = new IneqTree();

    Point p = new Point(x, y, z);

  	double n = sqrt(x*x + y*y + z*z);
  	double nx = x/n;
  	double ny = y/n;
  	double nz = z/n;

    p.X += 0.08 * nx;
    p.Y += 0.08 * ny;
    p.Z += 0.08 * nz;


    var m = ComputeBasis(nx, ny, nz, axis);

    res = res | new IneqTree((x, y, z) =>
    {
        Transform(ref x, ref y, ref z, p, m);

        x = x * 10;
        y = y * 10;
        z = z * 10;

        return sqrt(pow(pow(x, 2) + pow(y, 2) + pow(z, 2) / 2.25d, 3)) - pow(x, 2) - pow(y, 2) - pow(z, 2) / 2.25d;
    });
    return res;
}

public IneqMesh GetIneqMesh()
{
	double n = 1.0d/sqrt(2.0d);
	
  	var res = new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.5,
        Y1 = 1.5,
        Z1 = 1.0,
        D = 0.2d,
        Boxed = true,
        IneqTree =
		(
          Ball(0, 0, 0, 0.75d) | 
          Creature(henne, 0.75d, 0, 0) |
          Creature(hahn, 0, 0.75d, 0) |
          Creature(estbunny, 0.75d * n, 0.75d  * n, 0) |
          Egg(0.75d*cos(0.3d), 0, 0.75d*sin(0.3d), 'z') |
          Egg(0.75d*cos(-0.3d), 0, 0.75d*sin(-0.3d), 'x') |
          Egg(0, 0.75d*cos(-0.3d), 0.75d*sin(-0.3d), 'x') |
          Egg(cos(-0.3d) * 0.75d * n, cos(-0.3d) * 0.75d * n, 0.75d * sin(-0.3d), 'z')
		) &
		(# -x-y) &
		!Ball(0, 0, 0, 0.6d)          
          
      };
  
	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
		res.RefineTetrahedralMeshNearPointRedGreen(new Point[]
		{ 
          new Point(0.75d + 0.3d, 0, 0),
          new Point(0, 0.75d + 0.3d, 0),
          new Point(0.75d  * n + 0.3*n, 0.75d  * n + 0.3*n, 0),
          new Point(0.75d*cos(0.3d), 0, 0.75d*sin(0.3d)),
          new Point(0.75d*cos(-0.3d), 0, 0.75d*sin(-0.3d)),
          new Point(0, 0.75d*cos(-0.3d), 0.75d*sin(-0.3d)),
		  new Point(cos(-0.3d) * 0.75d * n, cos(-0.3d) * 0.75d * n, 0.75d * sin(-0.3d))            
		}, 0.4d, 2);
      
    };
  
  	res.PrepareBackgroundMesh = () => 
    {
      	res.RefineTetrahedralMeshByTetrahedrons(1, 5, 0.03);
      	res.RefineTetrahedralMeshByTetrahedrons(2, 5, 0.03);
      	res.RefineTetrahedralMeshByTetrahedrons(3, 10, 0.03);
    };
  
  	return res;    
}
