private SignedDistance henne = new SignedDistance("Henne.stl");
private SignedDistance hahn = new SignedDistance("Hahn.stl");

public IneqTree Bird(SignedDistance which, double x, double y, double z)
{
    IneqTree res = new IneqTree();
    Point p = new Point(x, y, z);
  
  	double n = sqrt(x*x + y*y +z*z);
  	double nx = x/n;
  	double ny = y/n;
  	double nz = z/n;
  
    p.X += 0.3 * nx;
    p.Y += 0.3 * ny;
    p.Z += 0.3 * nz;

    var m = ComputeBasis(nx, ny, nz, 'z');

    res = res | new IneqTree((x, y, z) =>
    {
        Transform(ref x, ref y, ref z, p, m);

        x = x * 3;
        y = y * 3;
        z = z * 3;

        return which.From(x,y,z);
    });
    return res;
}

public IneqMesh GetIneqMesh()
{
	double n = 0.75 / sqrt(2.0d * 0.75d * 0.75d);
	
  	var res = new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.5,
        Y1 = 1.5,
        Z1 = 1.0,
        D = 0.2d,
        Boxed = true,
        IneqTree =
		(
          Ball(0, 0, 0, 0.75d) | 
          Bird(henne, 0.75d, 0, 0) |
          Bird(henne, 0, 0.75d, 0) |
          //Ball(0.75d * n, 0.75d  * n, 0, 0.3)
          Bird(hahn, 0.75d * n, 0.75d  * n, 0)
		) &
		(# -x-y) &
		!Ball(0, 0, 0, 0.6d)          
          
      };
  
	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
		res.RefineTetrahedralMeshNearPointRedGreen(new Point[]
		{ 
          new Point(0.75d + 0.3d, 0, 0),
          new Point(0, 0.75d + 0.3d, 0),
          new Point(0.75d  * n + 0.3/sqrt(2), 0.75d  * n + 0.3/sqrt(2), 0)
		}, 0.35d, 2);
      
    };
  
  	res.PrepareBackgroundMesh = () => 
    {
      	res.RefineTetrahedralMeshByTetrahedrons(1, 5, 0.1);
      	res.RefineTetrahedralMeshByTetrahedrons(2, 5, 0.1);
      	res.RefineTetrahedralMeshByTetrahedrons(3, 5, 0.1);
    };
  
  	return res;    
}
