private FuncXYZ egg = (x,y,z) => sqrt(pow(pow(x, 2) + pow(y, 2) + pow(z, 2) / 2.25d, 3)) - pow(x, 2) - pow(y, 2) - pow(z, 2) / 2.25d;

private SignedDistance sdfHenne = new SignedDistance("Henne.stl");
private SignedDistance sdfHahn = new SignedDistance("Hahn.stl");

private IneqTree Creature(SignedDistance which, double x, double y, double z, double shift, out Point c, bool reflect = false)
{
	var b = new IneqTree(which.From);
    
  	var p = new Point(x, y, z);
  	ProjectToSurface(p, egg);
    
  	Gradient(egg, p, out double nx, out double ny, out double nz);

    var m = ComputeBasis(nx, ny, nz, 'z');
  
    p.X += shift * nx;
    p.Y += shift * ny;
    p.Z += shift * nz;
  	
  	b.Transform(delegate(ref double x, ref double y, ref double z) 
    {
		Transform(ref x, ref y, ref z, p, m);
        double a = PI / 2;	
        double x1 = x * cos(a) - y * sin(a);
        double y1 = x * sin(a) + y * cos(a);
        x = x1; y = y1;
      	if(reflect)
        {
          x = -x;			          
        }
      	x *= 2.0d; y *= 2.0d; z *= 2.0d;      	
    });
	
  	c = p;
	return b;  
}

private IneqTree SmallEgg(double x, double y, double z, char axis, out Point c)
{
	var b = new IneqTree(egg);
    
  	Point p = new Point(x, y, z);
  	ProjectToSurface(p, egg);
    
  	Gradient(egg, p, out double nx, out double ny, out double nz);

    var m = ComputeBasis(nx, ny, nz, axis);
  
    p.X += 0.1 * nx;
    p.Y += 0.1 * ny;
    p.Z += 0.1 * nz;
  	
  	b.Transform(delegate(ref double x, ref double y, ref double z) 
    {
		Transform(ref x, ref y, ref z, p, m);      	
      	x *= 8.0d; y *= 8.0d; z *= 8.0d;      	
    });
	
	c = p;
  	return b;  
}

public IneqMesh GetIneqMesh()
{
	var cock = Creature(sdfHahn, 1.0d, 0, 0, 0.4, out Point p1);
  	var hen1 = Creature(sdfHenne, 0.85, 0, 0.75, 0.4, out Point p2);
  	var hen2 = Creature(sdfHenne, 0.85, 0, -0.75, 0.35, out Point p3, true);
  
	var e1 = SmallEgg(0.85, 0.35, 0.75, 'z', out Point ep1);
	var e2 = SmallEgg(0.85, -0.3, 0.75, 'x', out Point ep2);
  	var e3 = SmallEgg(1.0, -0.35, 0, 'z', out Point ep3);
	var e4 = SmallEgg(0.85, -0.3, -0.75, 'x', out Point ep4); 
  	var e5 = SmallEgg(0.85, 0.35, -0.75, 'z', out Point ep5);
  
  	var subEgg = new IneqTree(egg);
  	subEgg.Transform(delegate(ref double x, ref double y, ref double z) 
    {
      	x *= 1.15d; y *= 1.15d; z *= 1.15d;      	
    });
  
  	var res = new IneqMesh
    {
        X0 = 0,
        Y0 = -1.0,
        Z0 = -1.5,
        X1 = 2.0,
        Y1 = 1.0,
        Z1 = 1.5,
        D = 0.2d,
        Boxed = true,
        IneqTree =
            (	egg | 
             	cock | hen1 | hen2 |
            	e1 | e2 | e3 | e4 | e5
            ) &
          	!subEgg
    };

	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
		res.RefineTetrahedralMeshNearPointRedGreen(new Point[]
		{ 
          p1, p2, p3
		}, 0.5d, 1);
		res.RefineTetrahedralMeshNearPointRedGreen(new Point[]
		{ 
          ep1, ep2, ep3, ep4, ep5
		}, 0.2d, 2);
      	res.RefineTetrahedralMesh(cock.Root.ExpressionIndex, 10, 0.05, false);
      	res.RefineTetrahedralMesh(hen1.Root.ExpressionIndex, 10, 0.05, false);
      	res.RefineTetrahedralMesh(hen2.Root.ExpressionIndex, 10, 0.05, false);    
    };
  
  	/*res.PrepareBackgroundMesh = () => 
    {
      	res.RefineTetrahedralMeshByTetrahedrons(cock.Root.ExpressionIndex, 2, 0.05, true);
      	res.RefineTetrahedralMeshByTetrahedrons(hen1.Root.ExpressionIndex, 2, 0.05, true);
      	res.RefineTetrahedralMeshByTetrahedrons(hen2.Root.ExpressionIndex, 2, 0.05, true);
    };*/
  
  	return res;  
}
