public static IneqTree Planes(int count)
{
    IneqTree res = new IneqTree();

    for (int i = 0; i < count; i++)
    {
        double x0 = 0.08d * Math.Cos(PI/10 + i * 2 * Math.PI / count);
        double y0 = 0.08d * Math.Sin(PI/10 + i * 2 * Math.PI / count);
        double z0 = 0;

        res = res & ((x, y, z) => (x - x0) * x0 + (y - y0) * y0 + (z - z0) * z0);
    }

  	res = res & (# -z);
  	res = res & (# z - 1.15);
  
  	res.Transform(delegate(ref double x, ref double y, ref double z)
	{
      	double f = 1 +  2*(1.5-z)*(1.5-z);
      
		x /= f;
      	y /= f;
       
    });

	res.Transform(delegate(ref double x, ref double y, ref double z) 
	{
		double x0 = 1.5;
      
      	double r = sqrt(z*z + (x-x0)*(x-x0));
      	double a = atan2(z, x-x0);
      
      	x = x0 - r;
      	z = (a < 0 ? -(a + PI) : (PI - a)) * r;    	
    });   
  
	res.Transform(delegate(ref double x, ref double y, ref double z) 
	{
      	x += 0.12;
      	z -= 0.1;
    });
  
    return res;
}

public IneqMesh GetIneqMesh()
{
   	var sdf = new SignedDistance("earth.stl");
	var f = sdf.From;//((double x, double y,double z) => x*x + y*y + z*z -1 );
  	var b = new IneqTree(f);
  
  	//var b = Ball(0, 0, 0, 1);
  
	b.Transform(delegate(ref double x, ref double y, ref double z) 
	{
      	double a = atan2(x, y);
      	double f = 0.75 + IneqTree.IneqNode.SmoothAbs(sin(6*a), 0.2)/6 + (1-z)/6;
      
		x /= f;
      	y /= f;
    });
  
	b.Transform(delegate(ref double x, ref double y, ref double z) 
	{      
      	double r = 3 * sqrt(x*x + y*y);
      
      	z = z * (1+exp(-r*r)/3);
      
    });   
  
    var res = new IneqMesh
    {
        X0 = -1.5,
        Y0 = -1.5,
        Z0 = -1.1,
        X1 = 1.5,
        Y1 = 1.5,
        Z1 = 1.5,
        D = 0.05d,
        Boxed = false,
        IneqTree =
          b |
          Planes(6)//.ToFunc(0.001)
	};
  
  	//res.PrepareBackgroundMeshBeforeApriory = () => res.RefineTetrahedralMesh(0, 10, 0.1);
  
  	return res;
}
