public static IneqTree Planes(int count)
{
    IneqTree res = new IneqTree();

    for (int i = 0; i < count; i++)
    {
        double x0 = 0.03d * Math.Cos(i * 2 * Math.PI / count);
        double y0 = 0.03d * Math.Sin(i * 2 * Math.PI / count);
        double z0 = 0;

        res = res & ((x, y, z) => (x - x0) * x0 + (y - y0) * y0 + (z - z0) * z0);
    }

  	res = res & (# -z);
  	res = res & (# z - 0.8);
  
  	res.Transform(delegate(ref double x, ref double y, ref double z)
	{
      	double f = 1 +  2*(1.2-z)*(1.2-z);
      
		x /= f;
      	y /= f;
       
    });

	res.Transform(delegate(ref double x, ref double y, ref double z) 
	{
		double x0 = 0.6;
      
      	if(x > x0)
        {
			x = x0;          
        }          
      
      	double r = sqrt(z*z + (x-x0)*(x-x0));
      	double a = atan2(z, x-x0);
      
      	x = x0 - r;
      	z = (a < 0 ? -(a + PI) : (PI - a)) * x0;    	
    });   
  
	res.Transform(delegate(ref double x, ref double y, ref double z) 
	{
      	//x += 0.12;
      	z -= 0.5;
      	x -= 0.27;
    });
  
    return res;
}

public IneqMesh GetIneqMesh()
{
  	var sdf = new SignedDistance("Shrek Head.stl");
	var b = (# sdf.From(x, y, z) - 0.05);   
  	//var b = Ball(0, 0, 0, 1);
  
	b.Transform(delegate(ref double x, ref double y, ref double z) 
	{
      	double a = atan2(x, y);
      	double f = 1.2 + IneqTree.IneqNode.SmoothAbs(sin(5*a), 0.2)/6 + (-1+z)/3;
      
		x /= f;
      	y /= f;
    });
  
	b.Transform(delegate(ref double x, ref double y, ref double z) 
	{      
      	double r = 6 * sqrt((x - 0.3)*(x - 0.3) + y*y);
      
		if(z > 0)	
      		z = z * (1+exp(-r*r)/4);      
    });   
  
    var res = new IneqMesh
    {
        X0 = -1.5,
        Y0 = -1.5,
        Z0 = -1.1,
        X1 = 1.5,
        Y1 = 1.5,
        Z1 = 1.5,
        D = 0.1d,
        Boxed = true,
        IneqTree =
          b |
          Planes(6)//.ToFunc(0.001)
	};
  
  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
        for (int i = 0; i < 2; i++)
        {
      		res.RefineTetrahedralMesh(b, 2, 0.03, false, 0.1);
      		res.JiggleBackgroundMash(3);
        }      	
    };
  
  	res.PrepareBackgroundMesh = () => 
    {
      	res.FindCrossEdges(0,2);
    };  


  
  	return res;
}
