static double SmoothSDF(double d, double eps = 0.1, double offset = 0.02)
{
	//return d/sqrt(1 + d*d/(eps*eps));
  	return eps * Math.Tanh((d + offset) / eps);
}


public IneqMesh GetIneqMesh()
{
   	var sdf = new SignedDistance("Keychain-Bicycle.stl");
	var f = ((double x, double y,double z) => /*SmoothSDF*/(sdf.From(x, y, z)+0.01));
  	var b = new IneqTree(f) ;

  	b.Transform(delegate(ref double x, ref double y, ref double z)
	{
		
      z -= 0.02;
      x *= 1.5; y *= 1.5; z *= 1.5;
    });

  
  	var heart =  IneqTreeParser.FromFormula(@"(2*z^2+y^2+x^2-1)^3<(0.1*z^2+y^2)*x^3") & (# z-0.05);
	heart.Transform(delegate(ref double x, ref double y, ref double z)
	{
      
      y += 0.3;
      
      double a = -PI/2;
      double x1 = x * cos(a) - y * sin(a);
      double y1 =x * sin(a) + y * cos(a);

      x = x1; y = y1;               
      x *= 1.5; y *= 1.5; z *= 1.5;
    });

  
    var res = new IneqMesh
    {
        X0 = -1,
        Y0 = -2.5,
        Z0 = -1,
        X1 = 1,
        Y1 = 1,
        Z1 = 1,
        D = 0.05d,
        Boxed = true,
      	MaxCheckCount = 5,
      	CheckDivisionCount = 2,
        IneqTree =
          b | heart
	};
  
  	//res.ProjectToSurfaceSpec[f] = sdf.Project;
  	res.PrepareBackgroundMeshBeforeApriory = () => res.RefineTetrahedralMesh(0, 4, 0.02);
  
  	return res;
}
