[advanced]
public IneqMesh GetIneqMesh()
{
    var ball = IneqLib.Ball(0.3, 0, 0, 0.45,4);

  	var cylinder1 = IneqLib.Cylinder(0.75, -0.65, -1,   0.75, -0.65, 1,    0.15, 0.3);
  	var cylinder2 = IneqLib.Cylinder(0.75,  0.65, -1,   0.75,  0.65, 1,    0.15, 0.3);
  	var cylinder3 = IneqLib.Cylinder(-0.5,  0,    -1,   -0.5,  0,    1,    0.15, 0.3);

	var heart =  IneqTreeParser.FromFormula(@"(2*z^2+y^2+x^2-1)^3<(0.1*z^2+y^2)*x^3");
  
    return new IneqMesh
    {
        X0 = -1.3,
      	Y0 = -1.2,
        Z0 = -1.0,
        X1 = 1.3,
      	Y1 = 1.2,
	    Z1 = 1.0,      
        D = 0.1d,
        Boxed = false,
        IneqTree = 
          	(
              (	
                  heart &
                  (# z-0)
              ) |
          	(cylinder1 | cylinder2 | cylinder3)
            ) &
          	ball.Not()
    };
}
