public IneqMesh GetIneqMesh()
{
  	double f = 8;
	
  	IneqTree b =  (#  x * sin(f*z) - y * cos(f*z));
  	IneqTree b1 = (# -x * sin(f*z) + y * cos(f*z));
  
  
	b.Transform(delegate(ref double x, ref double y, ref double z) 
	{
		var r = sqrt(x*x + y*y);      
      	z -= r/2;      
    });   
  
	b1.Transform(delegate(ref double x, ref double y, ref double z) 
	{
      	var r = sqrt(x*x + y*y);      
      	z += r/2;      
    });    
  
    return new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -1.5,
        X1 = 1.0,
        Y1 = 1.0,
        Z1 = 1.5,
        D = 0.1d,
        Boxed = true,
        IneqTree = ((b & b1) &         
          (# x*x+y*y-0.6)) |
          (# x*x+y*y-0.2)
          /*((IneqTree)((x, y, z) => x*Math.Sin(8*z) - y*Math.Cos(8*z)) &         
          ((x, y, z) => -x*Math.Sin(8*(z+0.2)) + y*Math.Cos(8*(z+0.2))) &         
          ((x,y,z) => x*x+y*y-0.9)) |
          ((x,y,z) => x*x+y*y-0.2)*/
          
      };
}
