public IneqMesh GetIneqMesh()
{
	double r = 1.0;
  	double c = 0.4d;
  
  	double z0 = 8.0;  
  	double t = z0 / c;  
  	double x0 = r * cos(t);
    double y0 = r * sin(t);
  
  	double nx = -r * sin(t);
  	double ny = r * cos(t);
	double nz = c;        
  
    IneqTree cutPlane = (# (x-x0)*nx + (y-y0)*ny + (z-z0)*nz);
  
  	return new IneqMesh
    {
        X0 = -1.8,
        Y0 = -1.8,
        Z0 = 0.0,
        X1 = 1.8,
        Y1 = 1.8,
        Z1 = z0 + 0.6d,
        D = 0.2d,
        Boxed = true,
        IneqTree =
		(
          	(# HelixDistance.Squared(x,y,z, r, c) - 0.5d) &
          	(cutPlane | (# z - (z0-0.6d)))
		) | 
		Ball(x0,y0,z0, 0.4d * r, 2)
      };
}
