[advanced]
public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -0.25,
        X1 = 1.0,
        Y1 = 1.0,
        Z1 = 0.25,
        D = 0.075d,
        Boxed = false,
        IneqTree = IneqLib.Planes(12) &
          ((x, y, z) => Math.Abs(z) - 0.25) &
          ((x, y, z) => -x * x  - y * y  + 0.1) 
      };
}
