[advanced]
public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -1.5,
        Y0 = -1.5,
        Z0 = -1,
        X1 = 1.5,
        Y1 = 1.5,
        Z1 = 5,
        D = 0.15d,
        Boxed = true,
        IneqTree = IneqTreeParser.FromFormula(@"
        (x-0.25*cos(4*z))^2+(y-0.25*sin(4*z))^2<0.85 &&
        (x-0.25*cos(4*z))^2+(y-0.25*sin(4*z))^2>0.3
        ")
    };
}
