SignedDistance sdf = new SignedDistance("Dodekaeder.stl");

public IneqTree Dodekaeder(int count, double step)
{
    IneqTree res = new IneqTree();

    for (int i = 0; i < count; i++)
    {
		var ii = i;
     	 res = res | (# sdf.From(x, y, z - step * ii)-0.05);
    }

    return res;
}

public static IneqTree Planes(int count)
{
    IneqTree res = new IneqTree();

    for (int i = 0; i < count; i++)
    {
        double x0 = 0.5d * Math.Cos(PI/10 + i * 2 * Math.PI / count);
        double y0 = 0.5d * Math.Sin(PI/10 + i * 2 * Math.PI / count);
        double z0 = 0;

        res = res & ((x, y, z) => (x - x0) * x0 + (y - y0) * y0 + (z - z0) * z0);
    }

    return res;
}

