private IneqTree Balls()
{
	var res = new IneqTree();	

  	var pol = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron(1.7);

    foreach (var v in pol.Vertices)
    {
		res = res | Ball(v.X, v.Y, v.Z, 0.3);
      	res = res | Cylinder(v.X, v.Y, v.Z, 0, 0, 0, 0.1, 0.7);
      
    }
  
  	foreach (var e in pol.Edges)
    {
		var v1 = pol.Vertices[e.Item1];
      	var v2 = pol.Vertices[e.Item2];
      	res = res | Cylinder(v1.X, v1.Y, v1.Z, v2.X, v2.Y, v2.Z, 0.1, 0.75);
    }
  
   	return res;
}

public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -2,
        Y0 = -2,
        Z0 = -2,
        X1 = 2,
        Y1 = 2,
        Z1 = 2,
        D = 0.08d,
        Boxed = true,
        IneqTree = Balls() |
          Ineq3DOnline.PlatonicSolids.Polyhedron.CreateIcosahedron(0.9).ToIneqTree()
          //.ToFunc(0.08)// & !(Ball(0,0,0,1))
	};
}
