private IneqTree Balls()
{
	var res = new IneqTree();	

  	var pol = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateIcosahedron(0.7);
  
  	foreach (var e in pol.Edges)
    {
		var v1 = pol.Vertices[e.Item1];
      	var v2 = pol.Vertices[e.Item2];
      	var v = 0.7 * ((v1 + v2)/2).Normalize();
      	res = res | Ball(v.X, v.Y, v.Z, 0.1);
    }
  
  	foreach (var f in pol.Faces)
    {
		var v1 = pol.Vertices[f[0]];
      	var v2 = pol.Vertices[f[1]];
      	var v3 = pol.Vertices[f[2]];
      	var v = 0.7 * ((v1 + v2 + v3)/3).Normalize();
      	res = res | Ball(v.X, v.Y, v.Z, 0.1);
    }

  
  	var res1 = new IneqTree();	  
  	foreach (var v in pol.Vertices)
    {
		res1 = res1 | Ball(v.X, v.Y, v.Z, 0.15);
      	//res = res | Cylinder(v.X, v.Y, v.Z, 0, 0, 0, 0.15, 0.7);      
    }
  
  	var res2 = new IneqTree();	  
  	foreach (var v in pol.Vertices)
    {
		res2 = res2 | Ball(0.9 * v.X, 0.9 * v.Y, 0.9 * v.Z, 0.14);
      	//res = res | Cylinder(v.X, v.Y, v.Z, 0, 0, 0, 0.15, 0.7);      
    }
  
  
  	res = ((res | Ball(0,0,0,0.7)) & !res1).ToFunc(0.04) | res2;
  
  
   	return res;
}

public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -1,
        Y0 = -1,
        Z0 = -1,
        X1 = 1,
        Y1 = 1,
        Z1 = 1,
        D = 0.08d,
        Boxed = true,
        IneqTree = Balls() //| Ball(0,0,0,0.8)
          //Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron(0.9).ToIneqTree()
          // & !(Ball(0,0,0,1))
	};
}
