private IneqTree TorusBall()
{
	var res = new IneqTree();	

  	var pol = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateIcosahedron();
  
  	foreach (var f in pol.Faces)
    {
      	var v1 = pol.Vertices[f[0]];
      	var v2 = pol.Vertices[f[1]];
      	var v3 = pol.Vertices[f[2]];
      
      	var v12 = ((v1 + v2) / 2).Normalize();
      	var v13 = ((v1 + v3) / 2).Normalize();
      	var v23 = ((v2 + v3) / 2).Normalize();
      
      	var v = ((v1 + v12  + v13) / 3).Normalize();      
      	res = res | Torus(v.X, v.Y, v.Z, 0.05, 0.15, v.X, v.Y, v.Z);
      
      	v = ((v2 + v12  + v23) / 3).Normalize();      
      	res = res | Torus(v.X, v.Y, v.Z, 0.05, 0.15, v.X, v.Y, v.Z);
      
      	v = ((v3 + v13  + v23) / 3).Normalize();      
      	res = res | Torus(v.X, v.Y, v.Z, 0.05, 0.15, v.X, v.Y, v.Z);

      	v = ((v12 + v13  + v23) / 3).Normalize();      
      	res = res | Torus(v.X, v.Y, v.Z, 0.05, 0.15, v.X, v.Y, v.Z);    
    }  
  
   	return res;
}

public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -1.8,
        Y0 = -1.8,
        Z0 = -1.8,
        X1 = 1.8,
        Y1 = 1.8,
        Z1 = 1.8,
        D = 0.05d,
        Boxed = false,
        IneqTree = TorusBall().ToFunc(0.003) //& !(Ball(0,0,0,1))
	};
}
