static void Normalize(double[,] verts)
{
  for (int i = 0; i < verts.GetLength(0); i++)
  {
    double x = verts[i, 0];
    double y = verts[i, 1];
    double z = verts[i, 2];
    double len = Math.Sqrt(x * x + y * y + z * z);
    verts[i, 0] /= len;
    verts[i, 1] /= len;
    verts[i, 2] /= len;
  }
}

private IneqTree Balls()
{
	var res = new IneqTree();	

  	var ico = Ineq3DOnline.PlatonicSolids.Polyhedron.CreateDodecahedron();

    foreach (var v in ico.Vertices)
    {
		res = res | Torus(v.X, v.Y, v.Z, 0.12, 0.35, v.X, v.Y, v.Z);
    }
  
   	return res;
}

public IneqMesh GetIneqMesh()
{
    return new IneqMesh
    {
        X0 = -1.8,
        Y0 = -1.8,
        Z0 = -1.8,
        X1 = 1.8,
        Y1 = 1.8,
        Z1 = 1.8,
        D = 0.1d,
        Boxed = false,
        IneqTree = Balls() //& !(Ball(0,0,0,1))
	};
}
