[advanced]
public IneqMesh GetIneqMesh()
{
    var res = new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.0,
        Y1 = 1.0,
        Z1 = 1.0,
        D = 0.1d,
        Boxed = false,
        IneqTree =
          (
          (# x * x + y * y + z * z - ((cos(12 * x) + cos(12 * y) + cos(12 * z)) / 6.0 + 0.6)) |
          (# x * x + y * y + z * z - ((sin(12 * x) + sin(12 * y) + sin(12 * z)) / 6.0 + 0.6))
        ) /*&
          (# x - 0.1)*/
      };
 

  	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      
        for (int i = 0; i < 2; i++)
        {
      		res.RefineTetrahedralMesh(0, 2, 0.05, false, 0);
          	res.RefineTetrahedralMesh(1, 2, 0.05, false, 0);
      		res.JiggleBackgroundMash(3);
        }     
      	
    };  
  
  	return res;  
}
