[advanced]

public IneqMesh GetIneqMesh()
{
    var res = new IneqMesh
    {
        X0 = -1.0064654,
        Y0 = -1.0444544,
        Z0 = -1.054564,
        X1 = 1.0465454,
        Y1 = 1.01424,
        Z1 = 1.0445,
        D = 0.1d,
        Boxed = true,
        IneqTree =
            (# sqrt(x*x+y*y) - 0.5 * z) 
      };

	res.PrepareBackgroundMeshBeforeApriory = () => 
    {
      var np = res.ForcePoint(new Point(0, 0, 0));

      var p1 = np.Points.Where(pp => pp.Z > 0).OrderBy(pp => pp.X * pp.X + pp.Y * pp.Y).First();
      p1.X = 0; p1.Y = 0; p1.Forced = true;  
      
      res.JiggleBackgroundMash(2);
    };     
  
  	return res;  
}
