public IneqMesh GetIneqMesh()
{
  	var  c = Cylinder(0, 0, -0.85, 0, 0, 0.85, rf: z => 0.15 + z / 5);
  
	c.Transform(delegate(ref double x, ref double y, ref double z) 
	{
		double x0 = 0.8;
      
      	double r = sqrt(z*z + (x-x0)*(x-x0));
      	double a = atan2(z, x-x0);
      
      	x = x0 - r;
      	z = (a < 0 ? -(a + PI) : (PI - a)) * r;
      	
      
      
    });
  
    return new IneqMesh
    {
        X0 = -1.0,
        Y0 = -1.0,
        Z0 = -1.0,
        X1 = 1.0,
        Y1 = 1.0,
        Z1 = 1.0,
        D = 0.1d,
        Boxed = true,
        IneqTree = c
      };
}
